import { platformBrowserDynamic } from '@angular/platform-browser-dynamic';
import { AppModule } from './app/app.module';
import * as $ from 'jquery';

$(window).scroll(() => {
  const scrollTop = $(window)?.scrollTop?.();
  if (scrollTop && scrollTop > 45) {
    $('.navbar').addClass('sticky-top shadow-sm');
  } else {
    $('.navbar').removeClass('sticky-top shadow-sm');
  }
});

const $dropdown = $(".dropdown");
const $dropdownToggle = $(".dropdown-toggle");
const $dropdownMenu = $(".dropdown-menu");
const showClass = "show";

$(window).on("load resize", function() {
  const windowWidth = $(window).width();
  if (windowWidth?.toExponential() >= 992) {
    $dropdown.hover(
      function() {
        const $this = $(this);
        $this.addClass(showClass);
        $this.find($dropdownToggle).attr("aria-expanded", "true");
        $this.find($dropdownMenu).addClass(showClass);
      },
      function() {
        const $this = $(this);
        $this.removeClass(showClass);
        $this.find($dropdownToggle).attr("aria-expanded", "false");
        $this.find($dropdownMenu).removeClass(showClass);
      }
    );
  } else {
    $dropdown.off("mouseenter mouseleave");
    $dropdownToggle.click(function(e) {
      e.preventDefault();
      const $this = $(this);
      const $parent = $this.parent(".dropdown");
      const isOpen = $parent.hasClass(showClass);
      $dropdown.removeClass(showClass);
      $dropdownToggle.attr("aria-expanded", "false");
      $dropdownMenu.removeClass(showClass);
      if (!isOpen) {
        $parent.addClass(showClass);
        $this.attr("aria-expanded", "true");
        $parent.find($dropdownMenu).addClass(showClass);
      }
    });
  }
});

platformBrowserDynamic().bootstrapModule(AppModule)
  .catch(err => console.error(err));
