import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-upload-image',
  templateUrl: './upload-image.component.html',
  styleUrls: ['./upload-image.component.css']
})
export class UploadImageComponent {
  maxFileSize: number = 1; // MB
  allowedExtensions: string[] = [".jpg", ".jpeg", ".png", ".gif", ".bmp"];
  uploadForm: FormGroup;

  constructor(private http: HttpClient, private formBuilder: FormBuilder) {
    this.uploadForm = this.formBuilder.group({
      fileInput: ['', Validators.compose([
        Validators.required,
        this.fileSizeValidator(),
        this.fileExtensionValidator()
      ])]
    });

  }

  onSubmit() {
    const formData = new FormData();
    formData.append('file', this.uploadForm.get('fileInput')?.value);
    this.http.post('https://localhost:7277/api/AzureImagesURL/UploadImage', formData)
      .subscribe((result: any) => {
        console.log(result);
        // Reset the form after successful upload
        this.uploadForm.reset();
      }, (error: any) => {
        console.error(error);
      });
  }

  private fileSizeValidator(control: any) {
    const file = control.value;
    if (file && file.size > 1024 * 1024 * this.maxFileSize) {
      return { fileSizeExceeded: true };
    }
    return null;
  }

  private fileExtensionValidator(control: any) {
    const file = control.value;
    const fileName = file?.name;
    const fileExtension = fileName?.split('.').pop();
    if (fileExtension && this.allowedExtensions.indexOf('.' + fileExtension.toLowerCase()) === -1) {
      return {
        invalidExtension: true
      };
    }
    return null;
  }

}
<form [formGroup]="uploadForm" (ngSubmit)="onSubmit()">
  <div class="form-group">
    <label for="fileInput">Select file:</label>
    <input type="file" id="fileInput" formControlName="fileInput">
    <div *ngIf="uploadForm.controls['fileInput'].errors?.['fileSizeExceeded'] && uploadForm.controls['fileInput'].touched"
         class="alert alert-danger">File size exceeds maximum limit of {{ maxFileSize }} MB.</div>
    <div *ngIf="uploadForm.controls['fileInput'].errors?.['required'] && uploadForm.controls['fileInput'].touched"
  class="alert alert-danger">Please select a file to upload.</div>

    <div *ngIf="uploadForm.controls['fileInput'].errors?.['invalidExtension'] && uploadForm.controls['fileInput'].touched"
         class="alert alert-danger">Invalid file extension. Allowed extensions: {{ allowedExtensions.join(', ') }}.</div>
  </div>
  <button type="submit" [disabled]="!uploadForm.valid" class="btn btn-primary">Upload</button>
</form>
