import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-upload-image',
  templateUrl: './upload-image.component.html',
  styleUrls: ['./upload-image.component.css']
})
export class UploadImageComponent {
  maxFileSize: number = 1; // MB
  allowedExtensions: string[] = [".jpg", ".jpeg", ".png", ".gif", ".bmp"];
  uploadForm: FormGroup;

  constructor(private http: HttpClient, private formBuilder: FormBuilder) {
    this.uploadForm = this.formBuilder.group({
      fileInput: ['', Validators.compose([Validators.required, this.fileSizeValidator.bind(this), this.fileExtensionValidator.bind(this)])],
      tourid: ['', Validators.required]
    });
  }

  onSubmit() {
    const formData = new FormData();
    formData.append('file', this.uploadForm.get('fileInput')?.value);
    this.http.post('https://localhost:7277/api/AzureImagesURL/UploadImage', formData)
      .subscribe((result: any) => {
        console.log(result);
        // Reset the form after successful upload
        this.uploadForm.reset();
      }, (error: any) => {
        console.error(error);
      });
  }

  private fileSizeValidator(control: any) {
    const file = control.value;
    if (file && file.size > 1024 * 1024 * this.maxFileSize) {
      return { fileSizeExceeded: true };
    }
    return null;
  }

  fileExtensionValidator(control: any) {
    const file = control.value;
    const fileName = file?.name;
    const fileExtension = fileName?.split('.').pop();
    if (fileExtension && this.allowedExtensions.indexOf('.' + fileExtension.toLowerCase()) === -1) {
      return {
        invalidExtension: true
      };
    }
    return null;
  }

}
<form class="d-flex align-items-center" (submit)="onSubmit()" [formGroup]="uploadForm">
  <div class="input-group mb-3">
    <label for="fileInput" class="input-group-text">Select image:</label>
    <input class="form-control" id="fileInput" type="file" name="fileInput" formControlName="fileInput">
    <input class="form-control" type="number" name="tourid" formControlName="tourid">
  </div>
  <button type="submit" class="btn btn-primary ml-1" [disabled]="!uploadForm.valid">Upload</button>
  <div *ngIf="uploadForm.controls['fileInput'].invalid && uploadForm.controls['fileInput'].touched">
    <p *ngIf="uploadForm.controls['fileInput'].errors?.['required']">File is required.</p>
    <p *ngIf="uploadForm.controls['fileInput'].errors?.['maxSize']">File exceeds maximum size limit of {{ maxFileSize }} MB.</p>
    <p *ngIf="uploadForm.controls['fileInput'].errors?.['invalidExtension']">File has an unsupported extension. Supported extensions are: {{ allowedExtensions.join(', ') }}.</p>
  </div>
  <div *ngIf="uploadForm.controls['tourid'].invalid && uploadForm.controls['tourid'].touched">
    <p *ngIf="uploadForm.controls['tourid'].errors?.['required']">Tour ID is required.</p>
  </div>
</form>
