import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-upload-images',
  templateUrl: './upload-images.component.html',
  styleUrls: ['./upload-images.component.css']
})
export class UploadImagesComponent {
  selectedFiles: File[] = [];
  uploadedUrls: any[] = [];
  maxFileSize: number = 1; // MB
  allowedExtensions: string[] = [".jpg", ".jpeg", ".png", ".gif", ".bmp"];

  constructor(private http: HttpClient) {}

  onSelect(event: any) {
    this.selectedFiles = event.target.files;
  }

  onSubmit() {
    const formData = new FormData();
    for (const file of this.selectedFiles) {
      if (this.checkFileSize(file) && this.checkIsImage(file)) {
        formData.append('files', file, file.name);
      } else {
        console.log(`File ${file.name} is invalid and will not be uploaded.`);
      }
    }

    if (formData.has('files')) {
      this.http.post('https://localhost:7277/api/AzureImagesURL/UploadImages', formData)
        .subscribe((result: any) => {
          console.log(result);
          this.uploadedUrls = result;
          // Reset the form after successful upload
          console.log(this.uploadedUrls[0].url);
          this.selectedFiles = [];
        }, (error: any) => {
          console.error(error);
        });
    } else {
      console.log('No valid files to upload.');
    }
  }

  private checkFileSize(file: File): boolean {
    if (file.size > 1024 * 1024 * this.maxFileSize) {
      console.error(`File ${file.name} exceeds size limit of ${this.maxFileSize} MB.`);
      return false;
    }
    return true;
  }

  private checkIsImage(file: File): boolean {
    if (!file.type.startsWith('image/') || !this.allowedExtensions.includes(`.${file.name.split('.').pop()}`)) {
      console.error(`File ${file.name} has an unsupported extension. Supported files are: ${this.allowedExtensions.join(', ')}`);
      return false;
    }
    return true;
  }
}
